// Normaliza payloads diversos em estrutura can√¥nica { quote, client, suppliers[], settings, template_data }
const inItem = (items && items[0] && items[0].json) ? items[0].json : {};

const out = { ...inItem };

// Normaliza quote e itens
const quote = out.quote || out.rfq || {};
const itemsCandidates = [
  Array.isArray(quote.items) ? quote.items : null,
  Array.isArray(out.items) ? out.items : null,
  Array.isArray(out.quote_items) ? out.quote_items : null,
  Array.isArray(out.template_data?.items) ? out.template_data.items : null,
].filter(Boolean);
quote.items = itemsCandidates[0] || [];
out.quote = quote;

// Normaliza cliente
out.client = out.client || out.client_info || out.organization || null;

// Normaliza fornecedores
let suppliers = Array.isArray(out.suppliers) ? out.suppliers : null;
if (!suppliers && out.supplier) suppliers = [out.supplier];
if (!suppliers) {
  const emails = out.supplier_emails || out.recipients?.emails || [];
  const whats = out.supplier_whatsapps || out.recipients?.whatsapps || [];
  const set = new Set();
  emails.forEach(e => e && set.add(`email:${e}`));
  whats.forEach(w => w && set.add(`wa:${w}`));
  suppliers = Array.from(set).map((key, idx) => {
    const [type, value] = key.split(':');
    return { id: `anon-${idx}`, name: 'Fornecedor', email: type === 'email' ? value : null, whatsapp: type === 'wa' ? value : null, phone: type === 'wa' ? value : null };
  });
}
out.suppliers = suppliers || [];

// Normaliza settings
out.settings = {
  send_email: Boolean(out.settings?.send_email ?? out.send_email ?? false),
  send_whatsapp: Boolean(out.settings?.send_whatsapp ?? out.send_whatsapp ?? true),
  custom_message: out.settings?.custom_message ?? out.custom_message ?? null,
  evolution: out.settings?.evolution ?? out.evolution ?? null,
  email: out.settings?.email ?? { from: out.email_from || null }
};

return [{ json: out }];

// Expande os fornecedores para m√∫ltiplos itens para processamento paralelo
// Entrada esperada do Webhook: { quote, client, suppliers: [...], settings, template_data }
const inItem = items[0].json;

if (!inItem.suppliers || !Array.isArray(inItem.suppliers) || inItem.suppliers.length === 0) {
  return items;
}

const out = inItem.suppliers.map((supplier) => ({
  json: {
    supplier,
    quote: inItem.quote,
    client: inItem.client,
    settings: inItem.settings || {},
    template_data: inItem.template_data || null
  }
}));

return out;

// Log para debug
console.log('Dados recebidos:', JSON.stringify($json, null, 2));

const { quote: inQuote, client: inClient, supplier, settings } = $json;

// Seguran√ßa: se faltar dados, descartar item
if (!inQuote || !inClient || !supplier) {
  console.error('Dados obrigat√≥rios faltando:', { quote: !!inQuote, client: !!inClient, supplier: !!supplier });
  return [];
}

// Garantir quote e itens
const quote = inQuote || {};
const fallbackItems =
  Array.isArray(quote.items) ? quote.items :
  Array.isArray($json.items) ? $json.items :
  Array.isArray($json.quote_items) ? $json.quote_items :
  Array.isArray($json.template_data?.items) ? $json.template_data.items :
  [];

const money = (v) => {
  try { return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(Number(v || 0)); }
  catch { return `R$ ${(Number(v || 0)).toFixed(2)}`; }
};
const dateBr = (d) => d ? new Date(d).toLocaleString('pt-BR') : 'A definir';

// Normaliza itens
const items = fallbackItems.map((i) => {
  const qty = Number(i.quantity || 0);
  const unit = Number(i.unit_price || 0);
  const total = Number(i.total != null ? i.total : unit * qty);
  return {
    product_name: i.product_name || i.name || 'Produto n√£o especificado',
    quantity: qty,
    unit_price: unit,
    total
  };
});
console.log('Items processados:', items.length);

const itens = items.map(i => `‚Ä¢ ${i.product_name} - Qtd: ${i.quantity} - Valor: ${money(i.total)}`).join('\n');
const itemsText = items.length > 0 ? `üì¶ *Itens:*\n${itens}\n` : 'üì¶ *Itens:* Consulte detalhes na cota√ß√£o\n';

const baseMessage = `üîî *Nova Cota√ß√£o Dispon√≠vel*\n\nüëã Ol√°, ${supplier.name || 'Fornecedor'}!\n\nüìã *${quote.title || 'Cota√ß√£o'}*\n${quote.description ? quote.description + '\n' : ''}\nüí∞ *Total Estimado:* ${money(quote.total || items.reduce((s,i)=>s+i.total,0))}\nüìÖ *Prazo:* ${dateBr(quote.deadline)}\n\n${itemsText}`;

const customMessage = settings?.custom_message;
let finalMessage = baseMessage;

if (customMessage) {
  finalMessage = customMessage
    .replace(/{{quote\.title}}/g, quote.title || 'Cota√ß√£o')
    .replace(/{{quote\.total}}/g, money(quote.total || items.reduce((s,i)=>s+i.total,0)))
    .replace(/{{quote\.deadline}}/g, dateBr(quote.deadline))
    .replace(/{{client\.name}}/g, inClient.name || 'Cliente')
    .replace(/{{supplier\.name}}/g, supplier.name || 'Fornecedor')
    .replace(/{{items_list}}/g, items.length > 0 ? itens : 'Consulte detalhes na cota√ß√£o');
}

// Rodap√© com dados do cliente
finalMessage += `\nüë§ *Cliente:* ${inClient.name || 'N√£o informado'}`;
finalMessage += `\nüìß *E-mail:* ${inClient.email || '‚Äî'}`;
if (inClient.phone) finalMessage += `\nüì± *Telefone:* ${inClient.phone}`;
finalMessage += '\n\n‚Äî\n_Cota√ß√£o enviada via QuoteMaster Pro_';

console.log('Mensagem final preparada:', finalMessage.substring(0, 200) + '...');

return {
  phone: supplier.whatsapp || supplier.phone,
  text: finalMessage,
  evolution: settings?.evolution || null
};
